routes-by-path.map:
function (doc) {
  var PATHSEP = "/";

  if (doc.namespace && doc.gatewayPath) {
    emit([doc.namespace, doc.gatewayPath], {"gatewayMethod":doc.gatewayMethod, "gatewayPath":doc.gatewayPath, "action":doc.action, "route-collection":doc["route-collection"], "apidoc":doc.apidoc});
  }
}

routes-by-api-name.map:
function (doc) {
  var PATHSEP = "/";

  if (doc.namespace && doc.apidoc && doc.apidoc.info && doc.apidoc.info.title) {
    if (! (doc._id.indexOf("STAGED_") == 0) ) {
      emit([doc.namespace, doc.apidoc.info.title], doc);
    }
  }
}

routes-by-namespace.map:
function (doc) {
  var PATHSEP = "/";

  if (doc.namespace && doc.apidoc && doc.apidoc.info && doc.apidoc.info.title) {
    if (! (doc._id.indexOf("STAGED_") == 0) ) {
        emit(doc.namespace, doc);
    }
  }
}
